// InstallButton.js

import React from 'react';
YOUR_GITHUB_OAUTH_URL=""
const InstallButton = () => {
  const handleInstall = () => {
    // Replace with your GitHub OAuth URL
    window.location.href = 'YOUR_GITHUB_OAUTH_URL';
  };

  return (
    <button onClick={handleInstall}>
      Install GitHub App
    </button>
  );
};

export default InstallButton;
