import React, { useState } from 'react';

import simpleGit from 'simple-git';



import { clone } from 'isomorphic-git';















const CloneRepositoryButton = () => {
  
    const [repositoryInfo, setRepositoryInfo] = useState({
    owner: 'harshit2024',
    repo: 'RestApi',
  });
  const [cloningStatus, setCloningStatus] = useState('');

  const handleCloneRepository = async () => {



    

    const repositoryURL = 'https://github.com/harshit2024/RestApi.git';
    const localPath = 'C:/Users/HarshitSrivastava/Desktop/clone';
    
   
           await clone({ dir: localPath,       url: repositoryURL,   singleBranch: true,      });
    
  };

  return (
    <div>
      <button onClick={handleCloneRepository}>Clone Repository</button>
      <p>{cloningStatus}</p>
    </div>
  );
};

export default CloneRepositoryButton;
